jQuery.fn.waterMark = function (waterMarkColor, normalColor) {
    var config1 = $.extend({}, { "waterMarkColor": waterMarkColor }, { "normalColor": normalColor });
    return this.each(function () {
        var waterMarkText = $(this).attr("water-text");
        if (waterMarkText.length == 0)
            waterMarkText = "Water mark here";
        var config = $.extend({}, { "waterMarkText": waterMarkText }, config1);
        config.waterMarkText = waterMarkText;
        if ($(this).val().length == 0) {
            $(this).val(config.waterMarkText);
            $(this).css("color", config.waterMarkColor);
        }
        $(this).bind("focus", function () {
            if ($(this).val() == config.waterMarkText) {
                $(this).val("");
                $(this).css("color", config.normalColor);
            }
        })
        $(this).bind("blur", function () {
            if ($(this).val().length == 0) {
                $(this).val(config.waterMarkText);
                $(this).css("color", config.waterMarkColor);
            }
        })
    });
};
